CREATE DEFINER=`root`@`localhost` PROCEDURE `repuestomasvendido`()
BEGIN
SELECT v.nombreRepuesto, COUNT(*) as total_ventas
FROM venta v
where precioRepuesto !=0.0 GROUP BY v.nombreRepuesto
ORDER BY total_ventas DESC;

END$$

